        [Fact]
        public void Part1Test()
        {
            var tests = new Dictionary<string, int>
            {
                {"(())", 0},
                {"()()", 0},
                {"(((", 3},
                {"(()(()(", 3},
                {"))(((((", 3},
                {"())", -1},
                {"))(", -1},
                {")))", -3},
                {")())())", -3},
                {Input, 232}
            };
            
            foreach (var test in tests)
            {
                Assert.Equal(test.Value, new Part1(test.Key).Compute());                
            }
        }

